CREATE DEFINER=`root`@`localhost` PROCEDURE `stuff_empata_grupos_de_ingles_ofertaacademica_a_alumnos` ()  BEGIN

declare v_clave int(11);
declare v_key_grupo varchar(2);
declare v_grupo varchar(5);
declare v_expediente int(11);

declare done int DEFAULT 0;
declare existe_grupo_alumno int(5) default 0;

declare ajustes cursor for
select clave,concat(SUBSTR(grupo,1,1),SUBSTR(grupo,4,1)) as key_grupo,grupo,expediente from ofertaacademica where materia like '%ingl%' and clave<800000;

declare continue HANDLER for SQLSTATE '02000' set done=1;

open ajustes;
REPEAT

	fetch ajustes into
	v_clave,
	v_key_grupo, 
	v_grupo,
	v_expediente;

			if not done then
				select count(*) from alumnos  where clave=v_clave and grupo=v_key_grupo and expediente=v_expediente and materia like 'INGLES%' into existe_grupo_alumno;
							
if (existe_grupo_alumno>0) then 
						update alumnos set grupo=v_grupo,observacion='LO ACTUALICE EN PROCESO DE CARGA GRUPOS DE INGLES' where clave=v_clave and grupo=v_key_grupo and expediente=v_expediente and materia like 'INGLES%';
							ELSE
						update ofertaacademica set observacion='grupos de ingles que no encontre alumnos' where clave=v_clave and grupo=v_grupo and expediente=v_expediente and materia like 'INGLES%' and clave<800000;

							end if;
			end if;


until done end repeat;
close ajustes;
END$$

