		DECLARE v_OrderDate date;
		DECLARE v_CustomerID VARCHAR(5);
		DECLARE acum INT;
		DECLARE fin INT DEFAULT 0;
		DECLARE o_cursor CURSOR FOR 
		SELECT CustomerID, OrderDate
		FROM orders	
		WHERE CustomerID = cid and EXTRACT(year from OrderDate)= odate;

		DECLARE CONTINUE HANDLER FOR NOT found SET fin = 1;
		SET acum = 0;
		OPEN o_cursor;
		c_ordenes: LOOP
			FETCH o_cursor INTO v_CustomerID, v_OrderDate;
			IF fin = 1 THEN	
				LEAVE c_ordenes;
			END IF;
			set acum = acum +1;
		END LOOP c_ordenes;
		CLOSE o_cursor;
		RETURN(acum);
END$$

